}

import { assert } from 'chai'
import createContext from './context'
import { forgetTokens } from 'state/actions/identity'
import { IS_DEV, IDLESTATUS_ACTIVE, NEXT_IDLE_STATUS_BLUEPRINT, START_BLUEPRINT, STOP_BLUEPRINT, RESET_BLUEPRINT, ACTIVITY_BLUEPRINT } from './constants'
import { bisectStore } from 'redux-mux'
import { publicBlueprints, nextIdleStatusBlueprint } from './blueprints'
import { createStartDetection } from './actions'
import { getNextIdleStatusIn } from './states'


/** When context has already been created, it can be shared to middleware component. */
export const createMiddleware = context => {
  const { log, activeStatusAction, idleStatusAction, translateBlueprintTypes, translateBlueprints, IDLE_STATUSES, idleStatusDelay } = context
  //const blueprintMiddleware = createBlueprintMiddleware(context)
  const { start, stop, reset } = translateBlueprints(publicBlueprints)
  const { nextIdleStatusAction } = translateBlueprints({ nextIdleStatusAction: nextIdleStatusBlueprint })
  const startDetection = createStartDetection(context)

  const { START
        , RESET
        , STOP
        , NEXT_IDLE_STATUS
        , ACTIVITY
        } = translateBlueprintTypes({ START: START_BLUEPRINT
                                    , RESET: RESET_BLUEPRINT
                                    , STOP: STOP_BLUEPRINT
                                    , NEXT_IDLE_STATUS: NEXT_IDLE_STATUS_BLUEPRINT
                                    , ACTIVITY: ACTIVITY_BLUEPRINT
                                    })

  const idleStatuses = [IDLESTATUS_ACTIVE, ...IDLE_STATUSES]
  const getNextIdleStatus = getNextIdleStatusIn(idleStatuses)
  const IDLESTATUS_FIRST = getNextIdleStatus(IDLESTATUS_ACTIVE)

  let stopDetection = null
  let nextTimeoutID = null
  return store => {
    const idleStore = bisectStore(store, 'idle')

    return next => action => {
      if(!action.type)
        return next(action)
      const { dispatch, getState } = store
      const { type, payload } = action

      const scheduleTransition = (idleStatus) => {
        clearTimeout(nextTimeoutID)
        let delay = dispatch(idleStatusDelay(idleStatus))
        assert.ok(delay, `must return an idle status delay for idleStatus === '${idleStatus}'`)
        assert.ok(typeof delay === 'number', `idle status delay must be a number type for idleStatus === '${idleStatus}'`)

        let lastActive = new Date().toTimeString()
        let nextMessage = `${NEXT_IDLE_STATUS} action continuing after ${delay} MS delay, lastActive: ${new Date().toTimeString()}`
        let nextCancelMessage = cancelledAt => `${NEXT_IDLE_STATUS} action cancelled before ${delay} MS delay by dispatcher, lastActive: ${new Date().toTimeString()}, cancelledAt: ${cancelledAt}`
        let nextIdleStatus = getNextIdleStatus(idleStatus)
        log.trace(`Scheduling next idle status '${idleStatus}' in ${delay} MS, then '${nextIdleStatus}'`)
        nextTimeoutID = setTimeout(() => {
          log.trace(nextMessage)
          next(action)
          dispatch(idleStatusAction(idleStatus))
          if(nextIdleStatus) {
            dispatch(nextIdleStatusAction(nextIdleStatus))
          } else {
            log.info('No more actions to schedule')
            // END OF THE LINE
          }
        }, delay)
        return function cancel() {
          clearTimeout(nextTimeoutID)
          log.trace(nextCancelMessage(new Date().toTimeString()))
        }
      }

      if(type === START) {
        stopDetection = dispatch(startDetection)
        let result = next(action)
        dispatch(nextIdleStatusAction(IDLESTATUS_FIRST))
        return result
      }

      if(type === RESET) {
        clearTimeout(nextTimeoutID)
        if(stopDetection)
          stopDetection()
        dispatch(start())
      }

      if(type === STOP && stopDetection) {
        clearTimeout(nextTimeoutID)
        dispatch(stopDetection)
      }

      if(type === NEXT_IDLE_STATUS) {
        return scheduleTransition(payload.nextIdleStatus)
      }

      if(type === ACTIVITY) {
        let result = next(action)
        if(payload.isTransition) {
          log.trace('Transition activity occurred, triggering user active action.')
          dispatch(activeStatusAction)
        }
        dispatch(nextIdleStatusAction(IDLESTATUS_FIRST))
        return result
      }
      return next(action)
    }
  }
}


/** Creates middleware from opts including validation in development */
export default function configureMiddleware(opts) { return createMiddleware(createContext(opts)) }

import { assert } from 'chai'
import { createStoreMultiplexer, bisectStore } from 'redux-mux'
import { createStore } from 'redux'
import  { IS_DEV
        , ROOT_STATE_KEY
        , ACTIVITY
        , ACTIVITY_DETECTION
        } from './constants'
import { createNoopStore, createLocalStore, configureReducer, createMergingReducer } from 'redux-addons/lib/store'


export const configureStoreMultiplexer = ({ useFastStore, useLocalStore }) => store => {
  const libStore = bisectStore(store, ROOT_STATE_KEY)
  let storesMapping = [ [ 'lib', libStore ] ]

  const createInitialFastState = () => ({ lastActive: +new Date(), lastEvent: { x: -1, y: -1 } })
  const createFastReducer = () => createMergingReducer(ACTIVITY)
  if(useFastStore)
    storesMapping.push([ 'fast', createStore(createFastReducer(), createInitialFastState()) ])

  const createInitialLocalState = () => ({ lastActive: +new Date() })
  const createLocalReducer = () => configureReducer(action => { lastActive: action.payload.lastActive }, false)(ACTIVITY)
  if(useLocalStore)
    storesMapping.push([ 'local', createLocalStore(createLocalReducer(), createInitialLocalState()) ])

  return createStoreMultiplexer(storesMapping)
}

import createContext from './context'
import  { IS_DEV, IDLESTATUS_ACTIVE, ACTIVITY_BLUEPRINT, ACTIVITY_DETECTION_BLUEPRINT, NEXT_IDLE_STATUS_BLUEPRINT } from './constants'
import { getNextIdleStatusIn } from './states'

/** When context has already been created, it can be shared to middleware component. */
export const createReducer = context => {
  const { log, blueprints, IDLE_STATUSES, initialState, translateBlueprintTypes, useFastStore, useLocalStore, useWebRTCState, useWebSocketsState } = context

  const { ACTIVITY
        , ACTIVITY_DETECTION
        , NEXT_IDLE_STATUS
        } = translateBlueprintTypes({ ACTIVITY: ACTIVITY_BLUEPRINT
                                    , ACTIVITY_DETECTION: ACTIVITY_DETECTION_BLUEPRINT
                                    , NEXT_IDLE_STATUS: NEXT_IDLE_STATUS_BLUEPRINT
                                    })

  const getNextIdleStatus = getNextIdleStatusIn([IDLESTATUS_ACTIVE, ...IDLE_STATUSES])

  return (state = initialState, action = {}) => {
    const { type, payload } = action
    switch(type) {
      case ACTIVITY:
        console.info(`REDUCER: ${ACTIVITY} => CURRENT[${state.idleStatus}]`)
        return Object.assign({}, state, getActivityPayload(payload))

      case ACTIVITY_DETECTION:
        console.info(`REDUCER: ${ACTIVITY_DETECTION} => CURRENT[${state.idleStatus}]`)
        return Object.assign({}, state, getActivityDetectionPayload(payload))

      case NEXT_IDLE_STATUS:
        console.info(`REDUCER: ${NEXT_IDLE_STATUS} => CURRENT[${state.idleStatus}], NEXT[${payload.nextIdleStatus}]`)
        if(payload.nextIdleStatus)
          return Object.assign({}, state, { idleStatus: payload.nextIdleStatus, isIdle: true, isPaused: false })
        console.info('REDUCER NO NEXT IDLE STATUS')
        return Object.assign({}, state, { isIdle: true, isPaused: true })

      default:
        return state
    }
  }
}

const getActivityPayload = ({ activeStatus, lastActive, lastEvent, timeoutID }) => ({ idleStatus: activeStatus, lastActive, lastEvent, timeoutID, isIdle: false })
const getActivityDetectionPayload = ({ isDetectionRunning }) => ({ isDetectionRunning })

/** Creates reducer from opts including validation in development */
export default function configureReducer (opts) { return createReducer(createContext(opts)) }

export const getNextIdleStatusIn = idleStatuses => idleStatus => {
  let nextIdleStatusIndex = idleStatuses.indexOf(idleStatus) + 1
  if(nextIdleStatusIndex < idleStatuses.length)
    return idleStatuses[nextIdleStatusIndex]
}
